pragma solidity ^0.8.2;
import "../libraries/ResourceUtils.sol";

contract ArmyTrainingFacet is ResourceUtils {
    /**
     * @notice Builds Army
     * @param _tokenId     Id of realm
     * @param _id       Id of Unit
     * @param _quantity      Quantity of units to train
     * @param _resourceIds  Resources to spend on units
     * @param _resourceValues  Resource values to spend
     */
    function buildRaidingArmy(
        uint256 _tokenId,
        uint32 _id,
        uint32 _quantity,
        uint256[] memory _resourceIds,
        uint256[] memory _resourceValues
    ) external payable checkSRealmOwner(_tokenId) {
        RaidingArmy storage raidingArmy = s.raidingArmy[_tokenId];
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        ArmyUnit memory armyUnit = s.armyUnit[_id];

        require(_quantity > 0, "MUST TRAIN MORE THAN 1");

        for (uint256 i = 0; i < armyUnit.resourceIds.length; i++) {
            require(
                _resourceIds[i] == armyUnit.resourceIds[i],
                "Incorrect resource"
            );
            require(
                _resourceValues[i] * _quantity ==
                    armyUnit.resourceValues[i] * _quantity,
                "Incorrect resources values"
            );
        }
        // checks castles are available
        uint256 totalPossibleUnits = realmInfo.castles * 5;

        require(
            (raidingArmy.knightsTrained - raidingArmy.knightsSlayed) +
                (raidingArmy.footSoldiersTrained -
                    raidingArmy.footSoldiersSlayed) +
                _quantity <=
                totalPossibleUnits,
            "NO_SPACE_FOR_UNIT"
        );

        if (_id == 0) {
            raidingArmy.knightsTrained += _quantity;
            for (uint256 i = 0; i < _quantity; i++) {
                uint256 lastUnitBuiltTime = raidingArmy
                    .knights[raidingArmy.knightsTrained]
                    .unitFinished > block.timestamp
                    ? raidingArmy
                        .knights[raidingArmy.knightsTrained]
                        .unitFinished
                    : block.timestamp;

                raidingArmy
                    .knights[raidingArmy.knightsTrained + 1]
                    .unitFinished = lastUnitBuiltTime + 4 hours;
            }
        } else if (_id == 1) {
            raidingArmy.footSoldiersTrained += _quantity;
            for (uint256 i = 0; i < _quantity; i++) {
                uint256 lastUnitBuiltTime = raidingArmy
                    .footSoldiers[raidingArmy.footSoldiersTrained]
                    .unitFinished > block.timestamp
                    ? raidingArmy
                        .footSoldiers[raidingArmy.footSoldiersTrained]
                        .unitFinished
                    : block.timestamp;

                raidingArmy
                    .footSoldiers[raidingArmy.footSoldiersTrained + 1]
                    .unitFinished = lastUnitBuiltTime + 4 hours;
            }
        }

        _batchBurnResources(msg.sender, _resourceIds, _resourceValues);
    }

    function getUnitCost(uint256 _unitId)
        public
        view
        returns (uint256[] memory, uint256[] memory)
    {
        ArmyUnit memory armyUnit = s.armyUnit[_unitId];

        return (armyUnit.resourceIds, armyUnit.resourceValues);
    }

    function getRaidingArmy(uint256 _tokenId)
        public
        view
        returns (uint256, uint256)
    {
        RaidingArmy storage raidingArmy = s.raidingArmy[_tokenId];

        return (
            raidingArmy.knightsTrained - raidingArmy.knightsSlayed,
            raidingArmy.footSoldiersTrained - raidingArmy.footSoldiersSlayed
        );
    }
}
