import "../../shared/interfaces/SRealmsToken.sol";
import "../libraries/LibAppStorage.sol";
import "../libraries/Modifiers.sol";
import "../../shared/interfaces/ResourcesIERC1155MintBurn.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

contract ResourceUtils is Modifiers {
    function _mintResources(
        address _minter,
        uint256[] memory _resourceIds,
        uint256[] memory _resourceValues
    ) internal {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        resourceTokens.resourceMinting(
            _minter,
            _resourceIds,
            _resourceValues,
            "0x"
        );
    }

    function _batchTransferResources(
        address _from,
        address _to,
        uint256[] memory _resourceIds,
        uint256[] memory _resourceValues
    ) internal {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        resourceTokens.safeBatchTransferFrom(
            _from,
            _to,
            _resourceIds,
            _resourceValues,
            "0x"
        );
    }

    function _batchBurnResources(
        address _from,
        uint256[] memory _resourceIds,
        uint256[] memory _resourceValues
    ) internal {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        resourceTokens.batchBurnResources(_from, _resourceIds, _resourceValues);
    }

    function _getHappiness(uint256 _realmId) internal view returns (uint32) {
        RealmInfo storage realmInfo = s.realmInfo[_realmId];
        int32 food;

        if (realmInfo.foodSupply < 0) {
            food = ((100 * realmInfo.foodSupply) * 20) / 1000;
        } else {
            food = 0;
        }
        int32 happiness = 100 +
            ((100 * (realmInfo.population * 4)) / 1000) +
            food;

        return uint32(happiness);
    }

    function _returnTime(uint256 _lastUpdate, uint256 _resourcesAvailable)
        internal
        pure
        returns (uint256)
    {
        return _lastUpdate + _resourcesAvailable * 1 hours;
    }

    function _getAvailableResources(uint256 _lastUpdate)
        internal
        view
        returns (uint256)
    {
        return (block.timestamp - _lastUpdate) / 1 hours;
    }

    function _getAvailableVestedResources(uint256 _vestedTimeUpdatedAt)
        internal
        view
        returns (uint256)
    {
        uint256 vested;
        vested = (block.timestamp - _vestedTimeUpdatedAt) / (1 hours);
        if (vested > 14) {
            return vested;
        } else {
            return 0;
        }
    }
}
