enId","type":"uint256"}],"name":"getRivers","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSRealmsAddress","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getTreasuryAddress","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"_tokenId","type":"uint256"}],"name":"getVestingTime","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"_tokenId","type":"uint256"}],"name":"getWonder","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/settling/facets/GetterFacet.sol":"GetterFacet"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":false,"runs":200},"remappings":[]},"sources":{"contracts/settling/facets/GetterFacet.sol":{"content":"// StakingFacet-License-Identifier: MIT
pragma solidity ^0.8.2;

import "../libraries/LibAppStorage.sol";

contract GetterFacet {
    AppStorage internal s;

    function getResourceAddress() public view returns (address) {
        return s.resources;
    }

    function getTreasuryAddress() public view returns (address) {
        return s.treasury;
    }

    function getRealmsAddress() public view returns (address) {
        return s.realms;
    }

    function getSRealmsAddress() public view returns (address) {
        return s.sRealms;
    }

    function getLordsAddress() public view returns (address) {
        return s.lords;
    }

    function getVestingTime(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return block.timestamp - realmInfo.lastUpdateAt;
    }

    function get30DayVestingTime(uint256 _tokenId)
        public
        view
        returns (uint256)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return block.timestamp - realmInfo.vestedTimeUpdatedAt;
    }

    function getAllStatistics(uint256 _realmId)
        public
        view
        returns (
            int32,
            int32,
            int32,
            int32,
            int32
        )
    {
        RealmInfo storage realmInfo = s.realmInfo[_realmId];
        return (
            realmInfo.realmDefence,
            realmInfo.realmOffence,
            realmInfo.realmMagic,
            realmInfo.foodSupply,
            realmInfo.population
        );
    }

    function getAllTraits(uint256 _tokenId)
        public
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return (
            realmInfo.regions,
            realmInfo.cities,
            realmInfo.harbours,
            realmInfo.rivers,
            realmInfo.orderOf,
            realmInfo.wonders
        );
    }

    function getName(uint256 _tokenId) public view returns (string memory) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.name;
    }

    function getRegions(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.regions;
    }

    function getCities(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.cities;
    }

    function getHarbours(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.harbours;
    }

    function getRivers(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.rivers;
    }

    function getResourceIds(uint256 _tokenId)
        public
        view
        returns (uint256[] memory)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds;
    }

    function getResourceLength(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds.length;
    }

    function getWonder(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.wonders;
    }

    function getAgeSettled(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.ageSettled;
    }

    function getAgeClaimed(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.ageClaimed;
    }

    function getRealmAgeInfo(uint256 _tokenId)
        public
        view
        returns (uint256, uint256)
    {
        return (getAgeSettled(_tokenId), getAgeClaimed(_tokenId));
    }

    function getPopulation(uint256 _tokenId) public view returns (int256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.population;
    }

    function getFoodSupply(uint256 _tokenId) public view returns (int256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.foodSupply;
    }
}
