struct AppStorage {
    mapping(uint256 => RealmInfo) realmInfo;
    address sRealms;
    address realms;
    address realmsStorage;
    address treasury;
    address resources;
    address oracle;
    address lords;
    mapping(uint256 => LevelProduction) levelProduction;
    mapping(uint256 => UpgradeCosts) upgradeCosts;
    mapping(uint256 => BuildingUpgradeCosts) buildingUpgradeCosts;
    mapping(uint256 => BuildingCosts) buildingCosts;
    mapping(uint256 => TheAges) theAges;
    uint256 genesisTime;
    mapping(uint256 => Raiding) raiding;
    mapping(uint256 => RaidingArmy) raidingArmy;
    mapping(uint256 => DefendingArmy) defendingArmy;
    mapping(uint256 => ArmyUnit) armyUnit;
}

struct TheAges {
    uint256 settledRealms;
    uint256 ageStart;
    uint256 ageFinish;
}

struct RealmInfo {
    string name;
    uint256 lastUpdateAt;
    uint256 vestedTimeUpdatedAt;
    uint256 ageSettled;
    uint256 ageClaimed;
    uint256[] resourceIds;
    uint8 cities;
    uint8 harbours;
    uint8 rivers;
    uint8 regions;
    uint8 wonders;
    uint8 orderOf;
    uint8 aquaducts;
    uint8 temples;
    uint8 castles;
    uint8 markets;
    uint8 ports;
    uint8 barracks;
    uint8 farms;
    uint8 shipyard;
    uint8 fishingVillage;
    int32 realmDefence;
    int32 realmOffence;
    int32 realmMagic;
    int32 foodSupply;
    int32 population;
    mapping(uint256 => ResourceInfo) resourceInfo;
}

struct ResourceInfo {
    uint8 id;
    uint8 level; // level of resource production
    uint8 production;
}

struct BuildingCosts {
    string name;
    uint32[] resourceIds;
    uint32[] resourceValues;
    uint32[] requirements;
    int32 defence;
    int32 offence;
    int32 magic;
    int32 population;
    int32 food;
}

struct BuildingUpgradeCosts {
    uint8[] realmBuildings;
    mapping(uint256 => BuildingCosts) buildingCosts;
}

struct LevelProduction {
    uint8 output;
}
struct UpgradeCosts {
    uint8[] resourceIds;
    mapping(uint256 => UpgradeCostLevels) upgradeCostLevels;
}

struct UpgradeCostLevels {
    uint256[] value;
}
struct Raiding {
    uint256 coolDownTime;
    uint256 totalRaids;
    uint256 successfulRaids;
}

struct RaidingArmy {
    uint32 knightsTrained;
    uint32 knightsSlayed;
    uint32 footSoldiersTrained;
    uint32 footSoldiersSlayed;
    mapping(uint256 => TrainedArmyUnit) knights;
    mapping(uint256 => TrainedArmyUnit) footSoldiers;
}

struct DefendingArmy {
    uint32 trebuchetsTrained;
    uint32 trebuchetsSlayed;
    uint32 scorpionsTrained;
    uint32 scorpionsSlayed;
    mapping(uint256 => TrainedArmyUnit) trebuchets;
    mapping(uint256 => TrainedArmyUnit) scorpions;
}

struct TrainedArmyUnit {
    bool destroyed;
    uint256 unitFinished;
}

struct ArmyUnit {
    string name;
    uint256 timeToTrain;
    uint32 offence;
    uint32 defence;
    uint256[] resourceIds;
    uint256[] resourceValues;
}

library LibAppStorage {
    function diamondStorage() internal pure returns (AppStorage storage ds) {
        assembly {
            ds.slot := 0
        }
    }

    function abs(int256 x) internal pure returns (uint256) {
        return uint256(x >= 0 ? x : -x);
    }
}
