pragma solidity ^0.8.2;
import "../libraries/LibAppStorage.sol";
import "../../shared/interfaces/SRealmsToken.sol";
import "../../shared/interfaces/LordsToken.sol";
import "../libraries/Modifiers.sol";

contract LordsClaimingFacet is Modifiers {
    function _getAgeSupply(uint256 _age)
        internal
        view
        returns (uint256 _lordsAvailable)
    {
        LordsToken lordsToken = LordsToken(s.lords);

        _lordsAvailable = lordsToken.getAgeDistribution(_age);
        return _lordsAvailable;
    }

    function getAge() external view returns (uint256) {
        return _getAge();
    }

    function _getAge() internal view returns (uint256) {
        return (block.timestamp - s.genesisTime) / (1 days);
    }

    function timeToNextAge() external view returns (uint256) {
        return ((block.timestamp - s.genesisTime) % (1 days)) - _getAge();
    }

    function claimLords(uint256 _realmId)
        external
        payable
        checkSRealmOwner(_realmId)
    {
        LordsToken lordsToken = LordsToken(s.lords);

        RealmInfo storage realmInfo = s.realmInfo[_realmId];

        uint256 currentAge = _getAge();

        uint256 totalEligbleLords;

        uint256 totalSettledRealms;

        for (uint256 i = 0; i < currentAge; i++) {
            totalSettledRealms += s.theAges[i].settledRealms;
        }

        for (uint256 x = realmInfo.ageClaimed; x < currentAge; x++) {
            uint256 supply = _getAgeSupply(x);
            totalEligbleLords += supply / totalSettledRealms;
            totalEligbleLords += supply % totalSettledRealms;
        }

        require(totalEligbleLords > 0, "NO_LORDS_AVAILABLE");

        realmInfo.ageClaimed = _getAge();

        lordsToken.mint(msg.sender, totalEligbleLords);

        emit LordsClaimed(msg.sender, totalEligbleLords);
    }

    function claimAllLords() external payable {
        LordsToken lordsToken = LordsToken(s.lords);
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);

        uint256 tokenBalanceOwner = sRealmstoken.balanceOf(msg.sender);

        uint256 currentAge = _getAge();

        uint256 totalEligbleLords;

        uint256 totalSettledRealms;

        for (uint256 i = 0; i < currentAge; i++) {
            totalSettledRealms += s.theAges[i].settledRealms;
        }

        for (uint256 i = 0; i < tokenBalanceOwner; i++) {
            RealmInfo storage realmInfo = s.realmInfo[
                sRealmstoken.tokenOfOwnerByIndex(msg.sender, i)
            ];

            for (uint256 x = realmInfo.ageClaimed; x < currentAge; x++) {
                uint256 supply = _getAgeSupply(x);
                totalEligbleLords += supply / totalSettledRealms;
                totalEligbleLords += supply % totalSettledRealms;
            }

            realmInfo.ageClaimed = _getAge();
        }
        require(totalEligbleLords > 0, "NO_LORDS_AVAILABLE");

        lordsToken.mint(msg.sender, totalEligbleLords);

        emit LordsClaimed(msg.sender, totalEligbleLords);
    }

    event LordsClaimed(address _claimer, uint256 _lordsClaimed);
}
