struct AppStorage {
    mapping(uint256 => RealmInfo) realmInfo;
    mapping(uint256 => RealmStatistics) realmStatistics;
    mapping(uint256 => RealmBuildings) realmBuildings;
    uint8 baseProductionRate;
    uint8 maxProductionRate;
    address sRealms;
    address realms;
    address treasury;
    address resources;
    address oracle;
    address lords;
    mapping(uint256 => LevelProduction) levelProduction;
    mapping(uint256 => UpgradeCosts) upgradeCosts;
    mapping(uint256 => BuildingUpgradeCosts) buildingUpgradeCosts;
    mapping(uint256 => BuildingCosts) buildingCosts;
    mapping(uint256 => TheAges) theAges;
    uint256 genesisTime;
}

struct TheAges {
    uint256 settledRealms;
    uint256 ageStart;
    uint256 ageFinish;
}

struct RealmStatistics {
    int32 realmDefence;
    int32 realmOffence;
    int32 realmMagic;
    int32 foodSupply;
    int32 population;
}

struct RealmInfo {
    string name;
    uint256 lastUpdateAt;
    uint256 vestedTimeUpdatedAt;
    uint256 ageSettled;
    uint256 ageClaimed;
    uint256[] resourceIds;
    uint8 cities;
    uint8 harbours;
    uint8 rivers;
    uint8 regions;
    uint8 wonders;
    uint8 orderOf;
    mapping(uint256 => ResourceInfo) resourceInfo;
}

struct ResourceInfo {
    uint8 id;
    uint8 level; // level of resource production
    uint8 production;
}

struct StatsInfo {
    uint8 id;
    uint8 value; // level of statistic
}

struct RealmBuildings {
    uint8 aquaducts;
    uint8 temples;
    uint8 castles;
    uint8 markets;
    uint8 ports;
    uint8 barracks;
    uint8 farms;
    uint8 shipyard;
    uint8 fishingVillage;
}

struct BuildingCosts {
    string name;
    uint32[] resourceIds;
    uint32[] resourceValues;
    uint32[] requirements;
    int32 defence;
    int32 offence;
    int32 magic;
    int32 population;
    int32 food;
}

struct BuildingUpgradeCosts {
    uint8[] realmBuildings;
    mapping(uint256 => BuildingCosts) buildingCosts;
}

struct LevelProduction {
    uint8 output;
}
struct UpgradeCosts {
    uint8[] resourceIds;
    mapping(uint256 => UpgradeCostLevels) upgradeCostLevels;
}

struct UpgradeCostLevels {
    uint256[] value;
}

library LibAppStorage {
    function diamondStorage() internal pure returns (AppStorage storage ds) {
        assembly {
            ds.slot := 0
        }
    }

    function abs(int256 x) internal pure returns (uint256) {
        return uint256(x >= 0 ? x : -x);
    }
}
