import "../../shared/interfaces/SRealmsToken.sol";
import "../libraries/LibAppStorage.sol";

contract Modifiers {
    AppStorage internal s;
    modifier checkSRealmOwner(uint256 _tokenId) {
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);
        require(
            msg.sender == sRealmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }
    modifier checkRealmOwner(uint256 _tokenId) {
        RealmsToken realmstoken = RealmsToken(s.realms);
        require(
            msg.sender == realmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }
}
