//____|#|_|#|____RAIDING FACET____|#|_|#|____//
// Allows one Realm to raid another
// Gives outcome based of Realm statistics plus a diceroll
// Burns some resources
// If raid is succesful buildings are Razed and vault is plundered

contract RaidingFacet is ResourceUtils {
    event RaidResult(
        uint256 raidResult,
        uint256 attackingRealm,
        uint256 defendingRealm,
        address attackerAddress,
        address defenderAddress,
        uint256 raidingUnitsLost,
        uint256 defendingUnitsLost,
        uint256[] resourcesIdsPillaged,
        uint256[] resourcesValuesPillaged,
        uint256 unitsCaptured
    );

    function raidRealm(uint256 _attackingTokenId, uint256 _defendingTokenId)
        public
        checkSRealmOwner(_attackingTokenId)
    {
        bool raidSuccessful;
        uint256 raidResult;
        uint32 unitsCaptured;

        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);

        address defendersAddress = sRealmstoken.ownerOf(_defendingTokenId);
        require(msg.sender != defendersAddress, "DONT_KILL_YOUR_PEOPLE");

        (raidSuccessful, raidResult, unitsCaptured) = _calculateOutcome(
            _attackingTokenId,
            _defendingTokenId
        );
        RealmInfo storage defendingRealmInfo = s.realmInfo[_defendingTokenId];
        RealmInfo storage attackingRealmInfo = s.realmInfo[_attackingTokenId];
        require(
            attackingRealmInfo.orderOf != defendingRealmInfo.orderOf,
            "CANT_ATTACK_SAME_ORDER"
        );
        if (raidSuccessful) {
            // calculate pillaged resource values
            uint256[] memory dRealmResourceIds = new uint256[](
                defendingRealmInfo.resourceIds.length
            );
            uint256[] memory dRealmResourceValues = new uint256[](
                defendingRealmInfo.resourceIds.length
            );

            (dRealmResourceIds, dRealmResourceValues) = _pillagedResources(
                _defendingTokenId,
                25,
                defendingRealmInfo
            ); // currently set at 50 TODO: be within a band limit and Raze some buildings

            _mintResources(msg.sender, dRealmResourceIds, dRealmResourceValues);
            emit RaidResult(
                raidResult,
                _attackingTokenId,
                _defendingTokenId,
                msg.sender,
                defendersAddress,
                0, // attacking units lost
                0, //defending units lost
                dRealmResourceIds, // resource ids
                dRealmResourceValues, // resource values captured
                0 // units captured
            );
        } else {
            emit RaidResult(
                raidResult,
                _attackingTokenId,
                _defendingTokenId,
                msg.sender,
                defendersAddress,
                unitsCaptured, // attacking units lost
                0, //defending units lost
                new uint256[](0), // resource ids
                new uint256[](0), // resource values captured
                unitsCaptured // units captured
            );
        }
    }

    function _calculateOutcome(
        uint256 _attackingTokenId,
        uint256 _defendingTokenId
    )
        internal
        returns (
            bool,
            uint256,
            uint32
        )
    {
        // Attacking Realm
        RaidingArmy storage raidingArmy = s.raidingArmy[_attackingTokenId];

        // require(
        //     raidingArmy.knightsTrained + raidingArmy.footSoldiersTrained > 0,
        //     "NO_UNITS"
        // );

        RaidingArmy storage defendingRaidingArmy = s.raidingArmy[
            _defendingTokenId
        ];
        DefendingArmy storage defendingArmy = s.defendingArmy[
            _defendingTokenId
        ];
        RealmInfo storage defendingRealm = s.realmInfo[_defendingTokenId];

        uint32 knights;
        uint32 footSoldiers;
        uint32 trebuchets;
        uint32 scorpions;

        (knights, footSoldiers) = _finishedRaidingUnits(raidingArmy);

        (trebuchets, scorpions) = _finishedDefendingUnits(defendingArmy);

        // Defending Realm
        uint32 defendersChance = (((uint32(defendingRealm.realmDefence) +
            (300 * trebuchets) +
            (100 * scorpions)) * 100) /
            (uint32(defendingRealm.realmDefence) +
                (300 * trebuchets) +
                (100 * scorpions) +
                (knights * 250) +
                (footSoldiers * 100)));

        if (_pickWinner() > defendersChance) {
            return (true, defendersChance, 0);
        } else {
            raidingArmy.knightsTrained -= knights / 2;
            raidingArmy.footSoldiersTrained -= footSoldiers / 2;
            defendingRaidingArmy.knightsTrained += knights / 4;
            defendingRaidingArmy.footSoldiersTrained += footSoldiers / 4;
            return (false, defendersChance, (knights / 2) + (footSoldiers / 2)); // total units captured
        }
    }

    function _getChance(
        RaidingArmy storage raidingArmy,
        DefendingArmy storage defendingArmy,
        RealmInfo storage defendingRealm
    ) internal view returns (uint32) {
        uint32 knights;
        uint32 footSoldiers;
        uint32 trebuchets;
        uint32 scorpions;
        (knights, footSoldiers) = _finishedRaidingUnits(raidingArmy);

        (trebuchets, scorpions) = _finishedDefendingUnits(defendingArmy);

        // Defending Realm
        uint32 defendersChance = (((uint32(defendingRealm.realmDefence) +
            (300 * trebuchets) +
            (100 * scorpions)) * 100) /
            (uint32(defendingRealm.realmDefence) +
                (300 * trebuchets) +
                (100 * scorpions) +
                (knights * 250) +
                (footSoldiers * 100)));
        return defendersChance;
    }

    function getChance(uint256 _attackingTokenId, uint256 _defendingTokenId)
        public
        view
        returns (uint32)
    {
        RaidingArmy storage raidingArmy = s.raidingArmy[_attackingTokenId];
        DefendingArmy storage defendingArmy = s.defendingArmy[
            _defendingTokenId
        ];
        RealmInfo storage defendingRealm = s.realmInfo[_defendingTokenId];

        return _getChance(raidingArmy, defendingArmy, defendingRealm);
    }

    function _totalUnits(
        uint32 _trained,
        uint32 _slayed,
        uint32 _unfinished
    ) private pure returns (uint32) {
        return (_trained - _slayed - _unfinished);
    }

    function _finishedRaidingUnits(RaidingArmy storage raidingArmy)
        private
        view
        returns (uint32, uint32)
    {
        uint32 unfinishedKnights;
        uint32 unfinishedFootsoldiers;
        for (
            uint32 i = raidingArmy.knightsTrained;
            i < raidingArmy.knightsTrained - raidingArmy.knightsSlayed;
            i--
        ) {
            if (raidingArmy.knights[i].unitFinished < block.timestamp) {
                break;
            } else {
                unfinishedKnights++;
            }
        }

        for (
            uint32 i = raidingArmy.footSoldiersTrained;
            i <
            raidingArmy.footSoldiersTrained - raidingArmy.footSoldiersSlayed;
            i--
        ) {
            if (raidingArmy.footSoldiers[i].unitFinished < block.timestamp) {
                break;
            } else {
                unfinishedFootsoldiers++;
            }
        }
        return (
            _totalUnits(
                raidingArmy.knightsTrained,
                raidingArmy.knightsSlayed,
                unfinishedKnights
            ),
            _totalUnits(
                raidingArmy.footSoldiersTrained,
                raidingArmy.footSoldiersSlayed,
                unfinishedFootsoldiers
            )
        );
    }

    function _finishedDefendingUnits(DefendingArmy storage defendingArmy)
        private
        view
        returns (uint32, uint32)
    {
        uint32 unfinishedTrebuchets;
        uint32 unfinishedScorpions;

        for (
            uint32 i = defendingArmy.trebuchetsTrained;
            i <
            defendingArmy.trebuchetsTrained - defendingArmy.trebuchetsSlayed;
            i--
        ) {
            if (defendingArmy.trebuchets[i].unitFinished < block.timestamp) {
                break;
            } else {
                unfinishedTrebuchets++;
            }
        }

        for (
            uint32 i = defendingArmy.scorpionsTrained;
            i < defendingArmy.scorpionsTrained - defendingArmy.scorpionsSlayed;
            i--
        ) {
            if (defendingArmy.scorpions[i].unitFinished < block.timestamp) {
                break;
            } else {
                unfinishedScorpions++;
            }
        }
        return (
            _totalUnits(
                defendingArmy.trebuchetsTrained,
                defendingArmy.trebuchetsSlayed,
                unfinishedTrebuchets
            ),
            _totalUnits(
                defendingArmy.scorpionsTrained,
                defendingArmy.scorpionsSlayed,
                unfinishedScorpions
            )
        );
    }

    function _pickWinner() private view returns (uint256) {
        return _random() % 100;
    }

    function pickWinner() public view returns (uint256) {
        return _pickWinner();
    }

    function _random() private view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.difficulty,
                        block.timestamp,
                        uint8(100)
                    )
                )
            );
    }

    function _pillagedResources(
        uint256 _tokenId,
        uint256 _percentage,
        RealmInfo storage realmInfo
    )
        private
        returns (
            uint256[] memory _ids,
            uint256[] memory _realmsResourcesOutputs
        )
    {
        uint256[] memory resourcesProduction = new uint256[](
            realmInfo.resourceIds.length
        );
        _realmsResourcesOutputs = new uint256[](realmInfo.resourceIds.length);

        // calculate the resources on the realm and the levels they are at
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            resourcesProduction[i] = realmInfo
                .resourceInfo[realmInfo.resourceIds[i]]
                .production;
        }

        uint256 total = _getAvailableVaultResources(
            realmInfo.vestedTimeUpdatedAt
        );
        uint256 happiness = _getHappiness(_tokenId);
        // Calculate total output of the Realm
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            _realmsResourcesOutputs[i] =
                ((((resourcesProduction[i] * total) * _percentage) / 100) *
                    happiness) /
                100; // replace with resourcesAvailable
        }

        realmInfo.vestedTimeUpdatedAt = _returnPillagedTime(
            realmInfo.vestedTimeUpdatedAt,
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt),
            _percentage
        );

        return (realmInfo.resourceIds, _realmsResourcesOutputs);
    }

    function _returnPillagedTime(
        uint256 _lastUpdate,
        uint256 _resourcesAvailable,
        uint256 _percentage
    ) internal pure returns (uint256) {
        return
            _lastUpdate + ((_resourcesAvailable * 1 hours) * _percentage) / 100;
    }

    function _getAvailableVaultResources(uint256 _vestedTimeUpdatedAt)
        internal
        view
        returns (uint256)
    {
        return (block.timestamp - _vestedTimeUpdatedAt) / (1 hours);
    }
}
