    uint256 ageStart;
    uint256 ageFinish;
}

struct RealmInfo {
    string name;
    uint256 lastUpdateAt;
    uint256 vestedTimeUpdatedAt;
    uint256 ageSettled;
    uint256 ageClaimed;
    uint256[] resourceIds;
    uint8 cities;
    uint8 harbours;
    uint8 rivers;
    uint8 regions;
    uint8 wonders;
    uint8 orderOf;
    uint8 aquaducts;
    uint8 temples;
    uint8 castles;
    uint8 markets;
    uint8 ports;
    uint8 barracks;
    uint8 farms;
    uint8 shipyard;
    uint8 fishingVillage;
    int32 realmDefence;
    int32 realmOffence;
    int32 realmMagic;
    int32 foodSupply;
    int32 population;
    mapping(uint256 => ResourceInfo) resourceInfo;
}

struct ResourceInfo {
    uint8 id;
    uint8 level; // level of resource production
    uint8 production;
}

struct BuildingCosts {
    string name;
    uint32[] resourceIds;
    uint32[] resourceValues;
    uint32[] requirements;
    int32 defence;
    int32 offence;
    int32 magic;
    int32 population;
    int32 food;
}

struct BuildingUpgradeCosts {
    uint8[] realmBuildings;
    mapping(uint256 => BuildingCosts) buildingCosts;
}

struct LevelProduction {
    uint8 output;
}
struct UpgradeCosts {
    uint8[] resourceIds;
    mapping(uint256 => UpgradeCostLevels) upgradeCostLevels;
}

struct UpgradeCostLevels {
    uint256[] value;
}
struct Raiding {
    uint256 coolDownTime;
    uint256 totalRaids;
    uint256 successfulRaids;
}

struct RaidingArmy {
    uint32 knightsTrained;
    uint32 knightsSlayed;
    uint32 footSoldiersTrained;
    uint32 footSoldiersSlayed;
