    mapping(uint256 => TrainedArmyUnit) footSoldiers;
}

struct DefendingArmy {
    uint32 trebuchetsTrained;
    uint32 trebuchetsSlayed;
    uint32 scorpionsTrained;
    uint32 scorpionsSlayed;
    mapping(uint256 => TrainedArmyUnit) trebuchets;
    mapping(uint256 => TrainedArmyUnit) scorpions;
}

struct TrainedArmyUnit {
    bool destroyed;
    uint256 unitFinished;
}

struct ArmyUnit {
    string name;
    uint256 timeToTrain;
    uint32 offence;
    uint32 defence;
    uint256[] resourceIds;
    uint256[] resourceValues;
}

