contract ResourceConstructionFacet {
    AppStorage internal s;

    // TODO: Check resource is actually on the Realm
    // TODO: Check owner of Realm
    function upgradeResource(
        uint256 _realmTokenId,
        uint256 _resourceId,
        uint256[] memory resources,
        uint256[] memory resourceValues
    ) external payable {
        RealmInfo storage realmInfo = s.realmInfo[_realmTokenId];
        UpgradeCosts storage upgradeCosts = s.upgradeCosts[_resourceId];

        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );

        // get level

        for (uint256 i = 0; i < upgradeCosts.resourceIds.length; i++) {
            require(
                resources[i] == upgradeCosts.resourceIds[i],
                "Incorrect resource"
            );
            require(
                resourceValues[i] ==
                    upgradeCosts
                        .upgradeCostLevels[
                            realmInfo.resourceInfo[_resourceId].level
                        ]
                        .value[i],
                "Incorrect resources values"
            );
        }

        resourceTokens.productionBuildingsBurn(
            msg.sender,
            resources,
            resourceValues
        );

        realmInfo.resourceInfo[_resourceId].level++;

        LevelProduction storage levelProduction = s.levelProduction[
            realmInfo.resourceInfo[_resourceId].level
        ];

        realmInfo.resourceInfo[_resourceId].production = levelProduction.output;

        emit ResourceUpgraded(
            msg.sender,
            _realmTokenId,
            _resourceId,
            resources,
            resourceValues
        );
    }

    function getProductionDetails(uint256 _tokenId, uint256 _resourceId)
        public
        view
        returns (uint256, uint256)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        return (
            realmInfo.resourceInfo[_resourceId].level,
            realmInfo.resourceInfo[_resourceId].production
        );
    }

    function getCosts(uint256 _resourceId, uint256 _level)
        public
        view
        returns (uint8[] memory, uint256[] memory)
    {
        UpgradeCosts storage upgradeCosts = s.upgradeCosts[_resourceId];

        return (
            upgradeCosts.resourceIds,
            upgradeCosts.upgradeCostLevels[_level].value
        );
    }

    event ResourceUpgraded(
        address lord,
        uint256 realmID,
        uint256 buildingID,
        uint256[] resourcesValues,
        uint256[] resourcesBurnt
    );
}
