contract ResourceConstructionFacet {
    AppStorage internal s;

    // TODO: add security to this
    function initResourceValues() public {
        // output levels
        s.levelProduction[1].output = 10;
        s.levelProduction[2].output = 14;
        s.levelProduction[3].output = 16;
        s.levelProduction[4].output = 18;
        s.levelProduction[5].output = 20;

        // resource ids
        s.upgradeCosts[1].resourceIds = [1, 2, 3];
        s.upgradeCosts[2].resourceIds = [1, 2, 3];
        s.upgradeCosts[3].resourceIds = [1, 2, 3];
        s.upgradeCosts[4].resourceIds = [1, 2, 3];

        // value costs
        s.upgradeCosts[1].upgradeCostLevels[1].value = [1, 1, 1];
        s.upgradeCosts[1].upgradeCostLevels[2].value = [2, 2, 2];
        s.upgradeCosts[1].upgradeCostLevels[3].value = [3, 3, 3];
        s.upgradeCosts[1].upgradeCostLevels[4].value = [4, 4, 4];
        s.upgradeCosts[1].upgradeCostLevels[5].value = [5, 5, 5];

        s.upgradeCosts[2].upgradeCostLevels[1].value = [1, 1, 1];
        s.upgradeCosts[2].upgradeCostLevels[2].value = [2, 2, 2];
        s.upgradeCosts[2].upgradeCostLevels[3].value = [3, 3, 3];
        s.upgradeCosts[2].upgradeCostLevels[4].value = [4, 4, 4];
        s.upgradeCosts[2].upgradeCostLevels[5].value = [5, 5, 5];
    }

    // TODO: Check resource is actually on the Realm
    // TODO: Check owner of Realm
    function upgradeResource(
        uint256 _realmTokenId,
        uint256 _resourceId,
        uint256[] memory resources,
        uint256[] memory resourceValues
    ) external payable {
        RealmInfo storage realmInfo = s.realmInfo[_realmTokenId];
        UpgradeCosts storage upgradeCosts = s.upgradeCosts[_resourceId];

        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );

        // get level

        for (uint256 i = 0; i < upgradeCosts.resourceIds.length; i++) {
            require(
                resources[i] == upgradeCosts.resourceIds[i],
                "Incorrect resource"
            );
            require(
                resourceValues[i] ==
                    upgradeCosts
                        .upgradeCostLevels[
                            realmInfo.resourceInfo[_resourceId].level
                        ]
                        .value[i],
                "Incorrect resources values"
            );
        }

        resourceTokens.productionBuildingsBurn(
            msg.sender,
            resources,
            resourceValues
        );

        realmInfo.resourceInfo[_resourceId].level++;

        LevelProduction storage levelProduction = s.levelProduction[
            realmInfo.resourceInfo[_resourceId].level
        ];
        realmInfo.resourceInfo[_resourceId].production = levelProduction.output;

        // TODO: Emit burn event
    }

    function getProductionDetails(uint256 _tokenId, uint256 _resourceId)
        public
        view
        returns (uint256, uint256)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        return (
            realmInfo.resourceInfo[_resourceId].level,
            realmInfo.resourceInfo[_resourceId].production
        );
    }

    //TODO: Reverse return so resourceIds are first
    function getCosts(uint256 _resourceId, uint256 _level)
        public
        view
        returns (uint256[] memory, uint8[] memory)
    {
        UpgradeCosts storage upgradeCosts = s.upgradeCosts[_resourceId];

        return (
            upgradeCosts.upgradeCostLevels[_level].value,
            upgradeCosts.resourceIds
        );
    }
}
