contract ResourceStakingFacet {
    AppStorage internal s;

    event Init(uint256 _tokenId);

    function getRealmInformation(uint256 _tokenId)
        public
        view
        returns (uint256 lastupdate)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.lastUpdateAt;
    }

    function stakeRealm(uint256 _tokenId, bool _update)
        public
        payable
        checkRealmOwner(_tokenId)
    {
        // init contracts
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        RealmStatistics storage realmStatistics = s.realmStatistics[_tokenId];
        RealmsToken realmstoken = RealmsToken(s.realms);
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);

        // mint sRealm
        sRealmstoken.mintFromStakingContract(msg.sender, _tokenId);

        // transfer realms token
        realmstoken.safeTransferFrom(msg.sender, s.treasury, _tokenId);

        // if update is false update lastUpdate else set up initial struct and trigger oracle
        if (!_update) {
            realmInfo.lastUpdateAt = block.timestamp;
        } else {
            // setup realmInfo Struct
            realmInfo.tokenId = _tokenId;
            realmInfo.lastUpdateAt = block.timestamp;
            realmInfo.vestedTimeUpdatedAt = block.timestamp;
            // realmInfo.foodSupply = 100; // set @ base of 100
            // Do we need to add the rest of the struct base stats here?
            // emit Init(_tokenId);
            //check if stats have been set before
            if (realmStatistics.foodSupply == 0) {
                realmStatistics.foodSupply = 100; // set @ base of 100
                realmStatistics.realmDefence = 1;
                realmStatistics.realmOffence = 1;
                realmStatistics.realmMagic = 1;
            }

            // Do we need to add the rest of the struct base stats here?
            emit Init(_tokenId);
        }
    }

    function init(
        uint256 _tokenId,
        string memory _name,
        uint8[] memory _traits,
        uint8[] memory _resources,
        uint8 _wonder
    ) public {
        //require(msg.sender == oracle, "Only The Oracle Can Update"); //commented out for testing
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        //Add Name
        realmInfo.name = _name;
        //Set Regions, Cities, Harbors, Rivers
        realmInfo.regions = _traits[0];
        realmInfo.cities = _traits[1];
        realmInfo.harbours = _traits[2];
        realmInfo.rivers = _traits[3];
        // create resource id array
        for (uint8 i = 0; i < _resources.length; i++) {
            realmInfo.resourceIds.push(_resources[i]);
        }
        //Add the resource ids with base level and production rate
        for (uint8 i = 0; i < _resources.length; i++) {
            realmInfo.resourceInfo[i].id = _resources[i];
            realmInfo.resourceInfo[i].level = 1;
            realmInfo.resourceInfo[i].production = 10; //TODO: might move this somewhere upgradeable
            realmInfo.resourceInfo[i].upgradeCostIds = [1, 2, 3]; //TODO: might move this somewhere upgradeable
            realmInfo.resourceInfo[i].upgradeCostValue = [2, 2, 2]; //TODO: might move this somewhere upgradeable
        }
        //Log wonder if wonder != 0
        if (_wonder != 0) {
            realmInfo.wonders = _wonder;
        }
    }

    function withdrawResources(uint256 _tokenId, bytes calldata _calldata)
        public
        payable
        checkSRealmOwner(_tokenId)
    {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        uint256[] memory resourcesProduction = new uint256[](
            realmInfo.resourceIds.length
        );
        uint256[] memory realmsResourcesOutputs = new uint256[](
            realmInfo.resourceIds.length
        );
        uint256[] memory treasuryResourcesOutputs = new uint256[](
            realmInfo.resourceIds.length
        );

        // calculate the resources on the realm and the levels they are at
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            resourcesProduction[i] = realmInfo.resourceInfo[i].production;
        }

        // require(
        //     _getAvailableResources(realmInfo.lastUpdateAt) +
        //         _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt) >
        //         1,
        //     "not enough resouces"
        // );

        // Calculate total output of the Realm
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            realmsResourcesOutputs[i] =
                ((resourcesProduction[i] * 1) * 80) /
                100; // replace with resourcesAvailable
            treasuryResourcesOutputs[i] =
                ((resourcesProduction[i] * 1) * 20) /
                100; // replace with resourcesAvailable
        }

        // return the remaining time
        realmInfo.lastUpdateAt = _returnTime(
            realmInfo.lastUpdateAt,
            _getAvailableResources(realmInfo.lastUpdateAt)
        );
        realmInfo.vestedTimeUpdatedAt = _returnTime(
            realmInfo.vestedTimeUpdatedAt,
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt)
        );

        // send to minter and mint
        resourceTokens.resourceMinting(
            msg.sender,
            realmInfo.resourceIds,
            realmsResourcesOutputs,
            treasuryResourcesOutputs,
            _calldata
        );

        emit ResourcesClaimed(realmInfo.resourceIds, realmsResourcesOutputs);
    }

    function _returnTime(uint256 _lastUpdate, uint256 _resourcesAvailable)
        internal
        pure
        returns (uint256 _time)
    {
        _time = _lastUpdate + _resourcesAvailable * 1 days;
        return _time;
    }

    function _getAvailableResources(uint256 _lastUpdate)
        internal
        view
        returns (uint256)
    {
        return (block.timestamp - _lastUpdate) / 1 days;
    }

    function _getAvailableVestedResources(uint256 _vestedTimeUpdatedAt)
        internal
        view
        returns (uint256)
    {
        uint256 vested;
        vested = (block.timestamp - _vestedTimeUpdatedAt) / 1 days;
        if (vested > 31) {
            vested = vested - 30;
        }
        return vested;
    }

    modifier checkSRealmOwner(uint256 _tokenId) {
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);
        require(
            msg.sender == sRealmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }
    modifier checkRealmOwner(uint256 _tokenId) {
        RealmsToken realmstoken = RealmsToken(s.realms);
        require(
            msg.sender == realmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }

    event ResourcesClaimed(uint256[], uint256[]);

    // modifier checkStakedRealm(uint256 _tokenId) {
