    // function withDrawAndExit(uint256 _sTokenId, bytes memory data) {
    //     realmToken.safeTransferFrom(this.address, msg.sender,  _sTokenId);
    //     claimResources(_sTokenId, data);
    //     sRealmToken.burn(_sTokenId);
    //     _stakedRealms[msg.sender].remove(_sTokenId);
    // }

    // function stakeLoot(uint256 _realmTokenId, uint256 _lootTokenId)
    //     public
    //     checkLootOwner(_lootTokenId)
    // {
    //     RealmInfo memory realmInfo = _realmInfo[_realmTokenId];

    //     realmInfo.resourceProductionTime = realmInfo.resourceProductionTime - 1;

    //     // TODO: Check if Realm slot is available
    //     // TODO: mint erc721 for Looter
    //     // Map looter to Realm
    //     // decrease resourceProductionTime on Realm
    // }

    // function withDrawLootYield(uint256 _realmTokenId, uint256 _lootTokenId)
    //     public
    // {
    //     RealmInfo memory realmInfo = _realmInfo[_realmTokenId];

    //     // require Loot holder to be owner
    //     // requre Loot to be staked on the Realm
    // }

    // function unStakeLoot(uint256 _realmTokenId, uint256 _lootTokenId) public {
    //     // check if Loot is staked on Realm
    //     // check if loot is owned by sender
    //     // IF Loot is staked
    //     // Burn token and send Loot token to sender
    // }

    // GETTER FUNCTIONS
    function getVestingTime(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return block.timestamp - realmInfo.lastUpdateAt;
    }

    function getName(uint256 _tokenId) public view returns (string memory) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.name;
    }

    function getRegions(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.regions;
    }

    function getCities(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.cities;
    }

    function getHarbours(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.harbours;
    }

    function getRivers(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.rivers;
    }

    function getResourceIds(uint256 _tokenId)
        public
        view
        returns (uint256[] memory)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds;
    }

    function getResourceLength(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds.length;
    }

    function getWonder(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.wonders;
    }
}
