        uint256[] memory _ids,
        uint256[] memory _amounts,
        bytes memory _data
    ) public checkIfOwnerOrDiamond {
        _batchMint(_to, _ids, _amounts, _data);
        _batchMint(treasury, _ids, _amounts, _data);
    }

