        treasury = _newTreasury;
    }

    function changeTax(uint256 _newTax) public onlyOwner {
        tax = _newTax;
    }

    modifier checkIfOwnerOrDiamond() {
        require(
            msg.sender == owner() || msg.sender == diamond,
            "NOT THE DIAMOND"
        );
        _;
    }

    function daoMinting(
        address _to,
        uint256[] memory _ids,
        uint256[] memory _amounts,
        bytes memory _data
    ) public onlyOwner {
        _batchMint(_to, _ids, _amounts, _data);
    }
