pragma solidity ^0.8.2;
import "../../shared/interfaces/RealmsToken.sol";
import "../../shared/interfaces/SRealmsToken.sol";
import "../../shared/interfaces/ResourcesIERC1155MintBurn.sol";
import "../libraries/LibAppStorage.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

contract StakingFacet {
    AppStorage internal s;

    event Init(uint256 _tokenId);
    event RealmStaked(address _lord, uint256 _realmStaked, uint256 _age);
    event RealmUnstaked(address _lord, uint256 _realmUnstaked, uint256 _age);
    event ResourcesClaimed(uint256[], uint256[]);
    event RealmData(
        uint256 _tokenId,
        string _name,
        uint8[] _traits,
        uint8[] _resources,
        uint8 _orderOf,
        uint8 _wonder
    );

    function getRealmInformation(uint256 _tokenId)
        public
        view
        returns (uint256 lastupdate)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.lastUpdateAt;
    }

    function stakeRealm(uint256 _tokenId, bool _update)
        public
        payable
        checkRealmOwner(_tokenId)
    {
        // init contracts
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        RealmStatistics storage realmStatistics = s.realmStatistics[_tokenId];
        RealmsToken realmstoken = RealmsToken(s.realms);
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);

        // mint sRealm
        sRealmstoken.mintFromStakingContract(msg.sender, _tokenId);

        // transfer realms token
        realmstoken.safeTransferFrom(msg.sender, s.treasury, _tokenId);

        uint256 currentAge = (block.timestamp - s.genesisTime) / (1 days);

        s.theAges[currentAge].settledRealms++;

        // if update is false update lastUpdate else set up initial struct and trigger oracle
        if (!_update) {
            realmInfo.lastUpdateAt = block.timestamp;
            realmInfo.vestedTimeUpdatedAt = block.timestamp;
            realmInfo.ageSettled = currentAge;
            realmInfo.ageClaimed = currentAge;
        } else {
            // setup realmInfo Struct
            realmInfo.lastUpdateAt = block.timestamp;
            realmInfo.vestedTimeUpdatedAt = block.timestamp;
            realmInfo.ageSettled = currentAge;
            realmInfo.ageClaimed = currentAge;
            // realmInfo.foodSupply = 100; // set @ base of 100

            //check if stats have been set before
            if (realmStatistics.foodSupply == 0) {
                realmStatistics.foodSupply = 0; // set @ base of 100
                realmStatistics.realmDefence = 1;
                realmStatistics.realmOffence = 1;
                realmStatistics.realmMagic = 1;
            }

            emit Init(_tokenId);
            emit RealmStaked(msg.sender, _tokenId, currentAge);
        }
    }

    function init(
        uint256 _tokenId,
        string memory _name,
        uint8[] memory _traits,
        uint8[] memory _resources,
        uint8 _orderOf,
        uint8 _wonder
    ) public {
        // require(msg.sender == s.oracle, "Only The Oracle Can Update"); //commented out for testing
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        //Add Name
        realmInfo.name = _name;

        // Set order
        realmInfo.orderOf = _orderOf;

        //Set Regions, Cities, Harbors, Rivers
        realmInfo.regions = _traits[0];
        realmInfo.cities = _traits[1];
        realmInfo.harbours = _traits[2];
        realmInfo.rivers = _traits[3];

        // create resource id array
        for (uint8 i = 0; i < _resources.length; i++) {
            realmInfo.resourceIds.push(_resources[i]);
        }

        //Add the resource ids with base level and production rate
        for (uint8 i = 0; i < _resources.length; i++) {
            realmInfo.resourceInfo[_resources[i]].id = _resources[i];
            realmInfo.resourceInfo[_resources[i]].level = 1;
            realmInfo.resourceInfo[_resources[i]].production = 10;
        }

        //Log wonder if wonder != 0
        if (_wonder != 0) {
            realmInfo.wonders = _wonder;
        }
        emit RealmData(_tokenId, _name, _traits, _resources, _orderOf, _wonder);
    }

    function withdrawSingleRealmResources(
        uint256 _tokenId,
        bytes calldata _calldata
    ) public payable checkSRealmOwner(_tokenId) {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        uint256[] memory resourcesProduction = new uint256[](
            realmInfo.resourceIds.length
        );
        uint256[] memory realmsResourcesOutputs = new uint256[](
            realmInfo.resourceIds.length
        );
        uint256[] memory treasuryResourcesOutputs = new uint256[](
            realmInfo.resourceIds.length
        );

        // calculate the resources on the realm and the levels they are at
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            resourcesProduction[i] = realmInfo
                .resourceInfo[realmInfo.resourceIds[i]]
                .production;
        }

        require(
            _getAvailableResources(realmInfo.lastUpdateAt) +
                _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt) >
                1,
            "not enough resouces"
        );

        uint256 total = _getAvailableResources(realmInfo.lastUpdateAt) +
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt);

        // Calculate total output of the Realm
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            realmsResourcesOutputs[i] =
                ((((resourcesProduction[i] * total) * 90) / 10) *
                    _getHappiness(_tokenId)) /
                100; // replace with resourcesAvailable
            treasuryResourcesOutputs[i] =
                ((((resourcesProduction[i] * total) * 10) / 90) *
                    _getHappiness(_tokenId)) /
                100; // replace with resourcesAvailable
        }

        // return the remaining time
        realmInfo.lastUpdateAt = _returnTime(
            realmInfo.lastUpdateAt,
            _getAvailableResources(realmInfo.lastUpdateAt)
        );
        realmInfo.vestedTimeUpdatedAt = _returnTime(
            realmInfo.vestedTimeUpdatedAt,
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt)
        );

        // send to minter and mint
        resourceTokens.resourceMinting(
            msg.sender,
            realmInfo.resourceIds,
            realmsResourcesOutputs,
            _calldata
        );

        emit ResourcesClaimed(realmInfo.resourceIds, realmsResourcesOutputs);
    }

    function _withdrawResources(uint256 _tokenId)
        private
        checkSRealmOwner(_tokenId)
        returns (
            uint256[] memory _ids,
            uint256[] memory _realmsResourcesOutputs
        )
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];

        uint256[] memory resourcesProduction = new uint256[](
            realmInfo.resourceIds.length
        );
        _realmsResourcesOutputs = new uint256[](realmInfo.resourceIds.length);

        // calculate the resources on the realm and the levels they are at
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            resourcesProduction[i] = realmInfo
                .resourceInfo[realmInfo.resourceIds[i]]
                .production;
        }

        uint256 total = _getAvailableResources(realmInfo.lastUpdateAt) +
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt);

        // Calculate total output of the Realm
        for (uint256 i = 0; i < realmInfo.resourceIds.length; i++) {
            _realmsResourcesOutputs[i] =
                ((((resourcesProduction[i] * 1) * 90) / 10) *
                    _getHappiness(_tokenId)) /
                100; // replace with resourcesAvailable
        }

        // return the remaining time
        realmInfo.lastUpdateAt = _returnTime(
            realmInfo.lastUpdateAt,
            _getAvailableResources(realmInfo.lastUpdateAt)
        );

        realmInfo.vestedTimeUpdatedAt = _returnTime(
            realmInfo.vestedTimeUpdatedAt,
            _getAvailableVestedResources(realmInfo.vestedTimeUpdatedAt)
        );

        return (realmInfo.resourceIds, _realmsResourcesOutputs);
    }

    function withdrawAllResources(bytes calldata _calldata) public payable {
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);

        uint256 tokenBalanceOwner = sRealmstoken.balanceOf(msg.sender);
        uint256[] memory _ids;
        uint256[] memory _settlerValues;

        uint256[] memory _totalIds = new uint256[](22);
        uint256[] memory _totalSettlerValues = new uint256[](22);

        for (uint256 i = 0; i < tokenBalanceOwner; i++) {
            (_ids, _settlerValues) = _withdrawResources(
                sRealmstoken.tokenOfOwnerByIndex(msg.sender, i)
            );

            for (uint256 x = 0; x < _ids.length; x++) {
                _totalIds[_ids[x]] = _ids[x];
                _totalSettlerValues[_ids[x]] += _settlerValues[x];
            }
        }
        resourceTokens.batchResourceMinting(
            msg.sender,
            _totalIds,
            _totalSettlerValues,
            _calldata
        );
    }

    function _getHappiness(uint256 _realmId) internal view returns (uint32) {
        RealmStatistics storage realmStatistics = s.realmStatistics[_realmId];
        int32 food;

        if (realmStatistics.foodSupply < 0) {
            food = ((100 * realmStatistics.foodSupply) * 20) / 1000;
        } else {
            food = 0;
        }
        int32 happiness = 100 +
            ((100 * (realmStatistics.population * 4)) / 1000) +
            food;

        return uint32(happiness);
    }

    function getHappiness(uint256 _realmId) public view returns (uint256) {
        return _getHappiness(_realmId);
    }

    function _returnTime(uint256 _lastUpdate, uint256 _resourcesAvailable)
        internal
        pure
        returns (uint256 _time)
    {
        _time = _lastUpdate + _resourcesAvailable * 1 hours;
        return _time;
    }

    function _getAvailableResources(uint256 _lastUpdate)
        internal
        view
        returns (uint256)
    {
        return (block.timestamp - _lastUpdate) / 1 hours;
    }

    function _getAvailableVestedResources(uint256 _vestedTimeUpdatedAt)
        internal
        view
        returns (uint256)
    {
        uint256 vested;
        vested = (block.timestamp - _vestedTimeUpdatedAt) / 1 hours;
        if (vested > 31 hours) {
            vested = vested - 30 hours;
        }
        return vested;
    }

    // TODO: We could add a withdraw here
    function unstakeAndExit(uint256 _sTokenId)
        public
        checkSRealmOwner(_sTokenId)
    {
        RealmsToken realmstoken = RealmsToken(s.realms);
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);
        RealmInfo storage realmInfo = s.realmInfo[_sTokenId];
        uint256 currentAge = (block.timestamp - s.genesisTime) / (1 days);
        // reset time
        realmInfo.lastUpdateAt = 0;
        realmInfo.vestedTimeUpdatedAt = 0;
        // transfer realm back to owner
        realmstoken.safeTransferFrom(s.treasury, msg.sender, _sTokenId);

        s.theAges[realmInfo.ageSettled].settledRealms--;

        sRealmstoken.burn(_sTokenId);
        emit RealmUnstaked(msg.sender, _sTokenId, currentAge);
    }

    // GETTER FUNCTIONS
    function getVestingTime(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return block.timestamp - realmInfo.lastUpdateAt;
    }

    function get30DayVestingTime(uint256 _tokenId)
        public
        view
        returns (uint256)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return block.timestamp - realmInfo.vestedTimeUpdatedAt;
    }

    function getAllStatistics(uint256 _realmId)
        public
        view
        returns (
            int32,
            int32,
            int32,
            int32,
            int32
        )
    {
        RealmStatistics storage realmStatistics = s.realmStatistics[_realmId];
        return (
            realmStatistics.realmDefence,
            realmStatistics.realmOffence,
            realmStatistics.realmMagic,
            realmStatistics.foodSupply,
            realmStatistics.population
        );
    }

    function getAllTraits(uint256 _tokenId)
        public
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return (
            realmInfo.regions,
            realmInfo.cities,
            realmInfo.harbours,
            realmInfo.rivers,
            realmInfo.orderOf,
            realmInfo.wonders
        );
    }

    function getName(uint256 _tokenId) public view returns (string memory) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.name;
    }

    function getRegions(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.regions;
    }

    function getCities(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.cities;
    }

    function getHarbours(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.harbours;
    }

    function getRivers(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.rivers;
    }

    function getResourceIds(uint256 _tokenId)
        public
        view
        returns (uint256[] memory)
    {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds;
    }

    function getResourceLength(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.resourceIds.length;
    }

    function getWonder(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.wonders;
    }

    function getAgeSettled(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.ageSettled;
    }

    function getAgeClaimed(uint256 _tokenId) public view returns (uint256) {
        RealmInfo storage realmInfo = s.realmInfo[_tokenId];
        return realmInfo.ageClaimed;
    }

    function getRealmAgeInfo(uint256 _tokenId)
        public
        view
        returns (uint256, uint256)
    {
        return (getAgeSettled(_tokenId), getAgeClaimed(_tokenId));
    }

    function getPopulation(uint256 _tokenId) public view returns (int256) {
        RealmStatistics storage realmStatistics = s.realmStatistics[_tokenId];
        return realmStatistics.population;
    }

    function getFoodSupply(uint256 _tokenId) public view returns (int256) {
        RealmStatistics storage realmStatistics = s.realmStatistics[_tokenId];
        return realmStatistics.foodSupply;
    }

    modifier checkSRealmOwner(uint256 _tokenId) {
        SRealmsToken sRealmstoken = SRealmsToken(s.sRealms);
        require(
            msg.sender == sRealmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }
    modifier checkRealmOwner(uint256 _tokenId) {
        RealmsToken realmstoken = RealmsToken(s.realms);
        require(
            msg.sender == realmstoken.ownerOf(_tokenId),
            "You do not own this Realm"
        );
        _;
    }
}
