import "../libraries/LibAppStorage.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "../interfaces/ResourcesIERC1155MintBurn.sol";

contract TraitConstructionFacet {
    AppStorage internal s;

    // TODO: add security to this
    function initBuilding() public {
        // output levels
        s.buildingCosts[0].name = "markets";
        s.buildingCosts[0].resourceIds = [1, 2, 3];
        s.buildingCosts[0].resourceValues = [2, 2, 2];
        s.buildingCosts[0].requirements = [1];

        s.buildingCosts[1].name = "acquaducts";
        s.buildingCosts[1].resourceIds = [1, 2, 3];
        s.buildingCosts[1].resourceValues = [2, 2, 2];
        s.buildingCosts[1].requirements = [3];

        s.buildingCosts[2].name = "castles";
        s.buildingCosts[2].resourceIds = [1, 2, 3];
        s.buildingCosts[2].resourceValues = [2, 2, 2];
        s.buildingCosts[2].requirements = [0];

        s.buildingCosts[3].name = "ports";
        s.buildingCosts[3].resourceIds = [1, 2, 3];
        s.buildingCosts[3].resourceValues = [2, 2, 2];
        s.buildingCosts[3].requirements = [2];
    }

    function createBuilding(
        uint256 _realmTokenId,
        uint256 _buildingID,
        uint256[] memory resources,
        uint256[] memory resourceValues
    ) external payable {
        uint8 foodImpact = 2;

        RealmInfo storage realmInfo = s.realmInfo[_realmTokenId];
        RealmBuildings storage realmBuildings = s.realmBuildings[_realmTokenId];
        BuildingCosts storage buildingCosts = s.buildingCosts[_buildingID];

        RealmStatistics storage realmStatistics = s.realmStatistics[
            _realmTokenId
        ];
        ResourcesIERC1155MintBurn resourceTokens = ResourcesIERC1155MintBurn(
            s.resources
        );

        //Should the this dynamic
        if (_buildingID == 0) {
            require(
                realmBuildings.markets <= realmInfo.cities,
                "No cities left to build on"
            );
        }
        if (_buildingID == 1) {
            require(
                realmBuildings.aquaducts <= realmInfo.rivers,
                "No rivers left to build on"
            );
        }
        if (_buildingID == 2) {
            require(
                realmBuildings.castles <= realmInfo.regions,
                "No regions left to build on"
            );
        }
        if (_buildingID == 3) {
            require(
                realmBuildings.ports <= realmInfo.harbours,
                "No harbours left to build on"
            );
        }
        // require(
        //     resources[0] == 1 || resources[1] == 2 || resources[2] == 3,
        //     "Incorrect resource"
        // );
        // require(
        //     resourceValues[0] == woodRequired ||
        //         resourceValues[1] == stoneRequired ||
        //         resourceValues[2] == obsidianRequired,
        //     "Not enough wood"
        // );
        for (uint256 i = 0; i < buildingCosts.resourceIds.length; i++) {
            require(
                resources[i] == buildingCosts.resourceIds[i],
                "Incorrect resource"
            );
            require(
                resourceValues[i] == buildingCosts.resourceValues[i],
                "Incorrect resources values"
            );
        }
        resourceTokens.productionBuildingsBurn(
            msg.sender,
            resources,
            resourceValues
        );

        realmBuildings.aquaducts++;
        realmStatistics.foodSupply += foodImpact;
    }

    // event CityBuilt(address owner, uint256 realm_id);
    function getAquaducts(uint256 _tokenId) public view returns (uint256) {
        RealmBuildings storage realmBuildings = s.realmBuildings[_tokenId];

        return realmBuildings.aquaducts;
    }

    function getBuildingCosts(uint256 _index)
        public
        view
        returns (
            string memory,
            uint8[] memory,
            uint8[] memory,
            uint8[] memory
        )
    {
        BuildingCosts storage buildingCosts = s.buildingCosts[_index];

        return (
            buildingCosts.name,
            buildingCosts.resourceIds,
            buildingCosts.resourceValues,
            buildingCosts.requirements
        );
    }
}
